export declare const ARWEAVE_GATEWAYS: readonly ["arweave.net", "arweave.dev", "g8way.io", "arweave-search.goldsky.com"];
export declare const graphQlSchemaString = "\ntype Query {\n  transaction(id: ID!): Transaction\n\n  transactions(\n    ids: [ID!]\n\n    owners: [String!]\n\n    recipients: [String!]\n\n    tags: [TagFilter!]\n\n    bundledIn: [ID!]\n\n    block: BlockFilter\n\n    first: Int = 10\n\n    after: String\n\n    sort: SortOrder = HEIGHT_DESC\n  ): TransactionConnection!\n  block(id: String): Block\n  blocks(\n    ids: [ID!]\n\n    height: BlockFilter\n\n    first: Int = 10\n\n    after: String\n\n    sort: SortOrder = HEIGHT_DESC\n  ): BlockConnection!\n}\ntype Owner {\n  address: String!\n  key: String!\n}\n\ntype Amount {\n  winston: String!\n  ar: String!\n}\n\ntype MetaData {\n  size: String!\n  type: String\n}\n\ntype Tag {\n  name: String!\n  value: String!\n}\n\n# Block Schema\ntype Block {\n  id: ID!\n  timestamp: Int!\n  height: Int!\n  previous: ID!\n}\n\ntype Parent {\n  id: ID!\n}\n\ntype Bundle {\n  # ID of the containing data bundle.\n  id: ID!\n}\n\ntype Transaction {\n  id: ID!\n  anchor: String!\n  signature: String!\n  recipient: String!\n  owner: Owner!\n  fee: Amount!\n  quantity: Amount!\n  data: MetaData!\n  tags: [Tag!]!\n\n  block: Block\n\n  parent: Parent @deprecated(reason: \"Use 'bundledIn'\")\n  bundledIn: Bundle\n}\n\ntype PageInfo {\n  hasNextPage: Boolean!\n}\n\ntype TransactionEdge {\n  cursor: String!\n  node: Transaction!\n}\n\ntype TransactionConnection {\n  pageInfo: PageInfo!\n  edges: [TransactionEdge!]!\n}\n\ntype BlockEdge {\n  cursor: String!\n  node: Block!\n}\n\ntype BlockConnection {\n  pageInfo: PageInfo!\n  edges: [BlockEdge!]!\n}\n\ninput TagFilter {\n  name: String\n\n  values: [String!]\n\n  op: TagOperator = EQ\n\n  match: TagMatch = EXACT\n}\n\nenum TagOperator {\n  EQ\n\n  NEQ\n}\n\n# The method used to determine if tags match.\nenum TagMatch {\n  EXACT\n  WILDCARD\n  FUZZY_AND\n  FUZZY_OR\n}\n\ninput BlockFilter {\n  min: Int\n\n  max: Int\n}\n\nenum SortOrder {\n  HEIGHT_ASC\n  HEIGHT_DESC\n}\n";
